import { test, expect } from '@playwright/test';
import { createLead, generateTestName, navigateToModule, fillLeadForm, generatePhone } from './fixtures/test-helpers';

/**
 * 绾跨储骞跺彂鎿嶄綔娴嬭瘯
 * 浣跨敤杈呭姪鍑芥暟绠€鍖栨祴璇曚唬鐮乗n */
test.describe('Lead Concurrency', () => {

        test('should handle simultaneous lead creation', async ({ page, context }) => {
                const phone = generatePhone();
                const name = generateTestName('骞跺彂鍒涘缓');

                const page1 = await context.newPage();
                const page2 = await context.newPage();

                await page1.goto('/leads');
                await page2.goto('/leads');
                await page1.waitForLoadState('networkidle');
                await page2.waitForLoadState('networkidle');

                // 椤甸潰1鍒涘缓绾跨储
                await page1.click('button:has-text("鏂板缓绾跨储")');
                await page1.waitForSelector('[role="dialog"], dialog');
                await fillLeadForm(page1, { name, phone });
                await page1.click('button:has-text("鍒涘缓绾跨储")');

                // 椤甸潰2鍚屾椂鍒涘缓鐩稿悓鎵嬫満鍙风殑绾跨储
                await page2.click('button:has-text("鏂板缓绾跨储")');
                await page2.waitForSelector('[role="dialog"], dialog');
                await fillLeadForm(page2, { name: name + '_2', phone }); // 鐩稿悓鎵嬫満鍙穃n                await page2.click('button:has-text("鍒涘缓绾跨储")');

                // 绛夊緟缁撴灉
                await page1.waitForTimeout(3000);
                await page2.waitForTimeout(3000);

                console.log('鉁?骞跺彂鍒涘缓绾跨储娴嬭瘯瀹屾垚');

                await page1.close();
                await page2.close();
        });

        test('should handle simultaneous lead assignment', async ({ page, context }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('骞跺彂鍒嗛厤') });

                const page1 = await context.newPage();
                const page2 = await context.newPage();

                await page1.goto(`/leads/${leadId}`);
                await page2.goto(`/leads/${leadId}`);
                await page1.waitForLoadState('networkidle');
                await page2.waitForLoadState('networkidle');

                // 涓や釜椤甸潰鍚屾椂灏濊瘯鍒嗛厤
                const assignBtn1 = page1.locator('button:has-text("鍒嗛厤")');
                const assignBtn2 = page2.locator('button:has-text("鍒嗛厤")');

                if (await assignBtn1.isVisible({ timeout: 3000 })) {
                        await assignBtn1.click();
                        await page1.waitForTimeout(500);
                        await page1.click('button:has-text("纭")');
                }

                if (await assignBtn2.isVisible({ timeout: 3000 })) {
                        await assignBtn2.click();
                        await page2.waitForTimeout(500);
                        await page2.click('button:has-text("纭")');
                }

                await page1.waitForTimeout(2000);
                console.log('鉁?骞跺彂鍒嗛厤绾跨储娴嬭瘯瀹屾垚');

                await page1.close();
                await page2.close();
        });

        test('should handle simultaneous lead updates', async ({ page, context }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('骞跺彂鏇存柊') });

                const page1 = await context.newPage();
                const page2 = await context.newPage();

                await page1.goto(`/leads/${leadId}`);
                await page2.goto(`/leads/${leadId}`);
                await page1.waitForLoadState('networkidle');
                await page2.waitForLoadState('networkidle');

                // 涓や釜椤甸潰鍚屾椂灏濊瘯缂栬緫
                const editBtn1 = page1.locator('button:has-text("缂栬緫璧勬枡")');
                const editBtn2 = page2.locator('button:has-text("缂栬緫璧勬枡")');

                if (await editBtn1.isVisible({ timeout: 3000 })) {
                        await editBtn1.click();
                        await page1.waitForTimeout(500);
                }

                if (await editBtn2.isVisible({ timeout: 3000 })) {
                        await editBtn2.click();
                        await page2.waitForTimeout(500);
                }

                await page1.waitForTimeout(2000);
                console.log('鉁?骞跺彂鏇存柊绾跨储娴嬭瘯瀹屾垚');

                await page1.close();
                await page2.close();
        });

        test('should handle simultaneous followup additions', async ({ page, context }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('骞跺彂璺熻繘') });

                const page1 = await context.newPage();
                const page2 = await context.newPage();

                await page1.goto(`/leads/${leadId}`);
                await page2.goto(`/leads/${leadId}`);
                await page1.waitForLoadState('networkidle');
                await page2.waitForLoadState('networkidle');

                // 涓や釜椤甸潰鍚屾椂娣诲姞璺熻繘
                const addBtn1 = page1.locator('button:has-text("娣诲姞璺熻繘")');
                const addBtn2 = page2.locator('button:has-text("娣诲姞璺熻繘")');

                if (await addBtn1.isVisible({ timeout: 3000 })) {
                        await addBtn1.click();
                        const textarea1 = page1.locator('textarea').first();
                        if (await textarea1.isVisible()) {
                                await textarea1.fill('璺熻繘璁板綍1');
                        }
                        await page1.click('button:has-text("淇濆瓨")');
                }

                if (await addBtn2.isVisible({ timeout: 3000 })) {
                        await addBtn2.click();
                        const textarea2 = page2.locator('textarea').first();
                        if (await textarea2.isVisible()) {
                                await textarea2.fill('璺熻繘璁板綍2');
                        }
                        await page2.click('button:has-text("淇濆瓨")');
                }

                await page1.waitForTimeout(2000);
                console.log('鉁?骞跺彂娣诲姞璺熻繘娴嬭瘯瀹屾垚');

                await page1.close();
                await page2.close();
        });

        test('should handle simultaneous lead conversions', async ({ page, context }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('骞跺彂杞寲') });

                const page1 = await context.newPage();
                const page2 = await context.newPage();

                await page1.goto(`/leads/${leadId}`);
                await page2.goto(`/leads/${leadId}`);
                await page1.waitForLoadState('networkidle');
                await page2.waitForLoadState('networkidle');

                // 涓や釜椤甸潰鍚屾椂灏濊瘯杞负瀹㈡埛
                const convertBtn1 = page1.locator('button:has-text("杞负瀹㈡埛")');
                const convertBtn2 = page2.locator('button:has-text("杞负瀹㈡埛")');

                if (await convertBtn1.isVisible({ timeout: 3000 }) && await convertBtn1.isEnabled()) {
                        await convertBtn1.click();
                }

                if (await convertBtn2.isVisible({ timeout: 3000 }) && await convertBtn2.isEnabled()) {
                        await convertBtn2.click();
                }

                await page1.waitForTimeout(2000);
                console.log('鉁?骞跺彂杞寲绾跨储娴嬭瘯瀹屾垚');

                await page1.close();
                await page2.close();
        });
});

import { test, expect } from '@playwright/test';
import { createLead, generateTestName, navigateToModule, fillLeadForm, generatePhone, confirmDialog } from './fixtures/test-helpers';
import * as fs from 'fs';
import * as path from 'path';

/**
 * 绾跨储鍘婚噸妫€鏌ユ祴璇昞r
 * 浣跨敤杈呭姪鍑芥暟绠€鍖栨祴璇曚唬鐮乗r
 */
test.describe('Lead Duplicate Check', () => {
    test.afterEach(async ({ page }, testInfo) => {
        if (testInfo.status !== testInfo.expectedStatus) {
            const dir = 'test-results';
            if (!fs.existsSync(dir)) fs.mkdirSync(dir, { recursive: true });
            const baseName = `${testInfo.project.name}-${testInfo.title}`.replace(/[^a-zA-Z0-9]/g, '-').toLowerCase();
            await page.screenshot({ path: path.join(dir, `${baseName}.png`), fullPage: true });
        }
    });

    test('should prevent duplicate lead creation for active leads', async ({ page }) => {
        const phone = generatePhone();
        const name = generateTestName('鍘婚噸娴嬭瘯');

        await navigateToModule(page, 'leads');

        // 绗竴娆″垱寤虹嚎绱r
        await createLead(page, { name, phone });
        console.log('鉁?绗竴涓嚎绱㈠垱寤烘垚鍔?);

        // 灏濊瘯鍒涘缓閲嶅绾跨储
        await page.click('button:has-text("鏂板缓绾跨储")');
        await page.waitForSelector('[role="dialog"], dialog');
        await fillLeadForm(page, { name: name + '_Dupe', phone }); // 鐩稿悓鎵嬫満鍙穃r
        await page.click('button:has-text("鍒涘缓绾跨储")');

