import { test, expect } from '@playwright/test';
import { createLead, generateTestName, navigateToModule } from './fixtures/test-helpers';

/**
 * 绾跨储璺熻繘鎻愰啋娴嬭瘯
 * 浣跨敤杈呭姪鍑芥暟绠€鍖栨祴璇曚唬鐮乗n */
test.describe('Lead Followup Reminders', () => {

        /**
         * 娣诲姞璺熻繘璁板綍鐨勮緟鍔╁嚱鏁癨n         */
        async function addFollowup(page: import('@playwright/test').Page, content: string, nextFollowupDays?: number): Promise<void> {
                const addFollowupBtn = page.locator('button:has-text("娣诲姞璺熻繘")');
                if (await addFollowupBtn.isVisible({ timeout: 3000 })) {
                        await addFollowupBtn.click();
                        await page.waitForTimeout(500);

                        const textarea = page.locator('textarea').first();
                        if (await textarea.isVisible()) {
                                await textarea.fill(content);
                        }

                        // 璁剧疆涓嬫璺熻繘鏃堕棿
                        if (nextFollowupDays !== undefined) {
                                const dateInput = page.locator('input[type="datetime-local"], input[type="date"]');
                                if (await dateInput.isVisible({ timeout: 2000 })) {
                                        const nextDate = new Date(Date.now() + nextFollowupDays * 86400000);
                                        await dateInput.fill(nextDate.toISOString().slice(0, 16));
                                }
                        }

                        await page.click('button:has-text("淇濆瓨")');
                        await page.waitForTimeout(500);
                }
        }

        test('should set next followup reminder', async ({ page }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('璺熻繘鎻愰啋') });

                await page.goto(`/leads/${leadId}`);
                await page.waitForLoadState('networkidle');

                await addFollowup(page, '璁剧疆璺熻繘鎻愰啋娴嬭瘯', 1); // 鏄庡ぉ璺熻繘
                console.log('鉁?璺熻繘鎻愰啋璁剧疆鎴愬姛');
        });

        test('should display followup reminder on lead list', async ({ page }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('鍒楄〃鎻愰啋') });

                await page.goto(`/leads/${leadId}`);
                await page.waitForLoadState('networkidle');

                await addFollowup(page, '鍒楄〃鏄剧ず娴嬭瘯', 1);

                // 杩斿洖鍒楄〃楠岃瘉
                await navigateToModule(page, 'leads');
                console.log('鉁?鍒楄〃璺熻繘鎻愰啋鏄剧ず娴嬭瘯瀹屾垚');
        });

        test('should display expired followup reminder', async ({ page }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('杩囨湡鎻愰啋') });

                await page.goto(`/leads/${leadId}`);
                await page.waitForLoadState('networkidle');

                // 娉ㄦ剰锛氳缃繃鏈熸椂闂达紙璐熸暟澶╂暟锛塡n                await addFollowup(page, '杩囨湡鎻愰啋娴嬭瘯', -1);
                console.log('鉁?杩囨湡璺熻繘鎻愰啋娴嬭瘯瀹屾垚');
        });

        test('should complete followup reminder', async ({ page }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('瀹屾垚璺熻繘') });

                await page.goto(`/leads/${leadId}`);
                await page.waitForLoadState('networkidle');

                // 娣诲姞绗竴鏉¤窡杩沑n                await addFollowup(page, '绗竴娆¤窡杩?, 1);

                // 娣诲姞瀹屾垚璺熻繘
                await addFollowup(page, '瀹屾垚璺熻繘');
                console.log('鉁?瀹屾垚璺熻繘娴佺▼娴嬭瘯鎴愬姛');
        });

        test('should handle multiple followup reminders', async ({ page }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('澶氭璺熻繘') });

                await page.goto(`/leads/${leadId}`);
                await page.waitForLoadState('networkidle');

                // 娣诲姞澶氭潯璺熻繘璁板綍
                for (let i = 0; i < 3; i++) {
                        await addFollowup(page, `璺熻繘璁板綍 ${i + 1}`, i + 1);
                }
                console.log('鉁?澶氭璺熻繘鎻愰啋娴嬭瘯瀹屾垚');
        });

        test('should handle followup reminder with special characters', async ({ page }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('鐗规畩瀛楃') });

                await page.goto(`/leads/${leadId}`);
                await page.waitForLoadState('networkidle');

                const specialContent = '娴嬭瘯鍐呭@#$%^&*()_+-={}[]|\\:;"\'<>?,./~`';
                await addFollowup(page, specialContent, 1);
                console.log('鉁?鐗规畩瀛楃璺熻繘鍐呭娴嬭瘯鎴愬姛');
        });

        test('should handle followup reminder with long note', async ({ page }) => {
                await navigateToModule(page, 'leads');
                const leadId = await createLead(page, { name: generateTestName('闀垮唴瀹?) });

                await page.goto(`/leads/${leadId}`);
                await page.waitForLoadState('networkidle');

                const longContent = 'A'.repeat(500);
                await addFollowup(page, longContent, 1);
                console.log('鉁?闀垮唴瀹硅窡杩涙祴璇曟垚鍔?);
        });
});

import { test, expect } from '@playwright/test';
