'use client';

import React, { useState, useCallback } from 'react';
import Upload from 'lucide-react/dist/esm/icons/upload';
import AlertCircle from 'lucide-react/dist/esm/icons/alert-circle';
import CheckCircle2 from 'lucide-react/dist/esm/icons/check-circle-2';
import Loader2 from 'lucide-react/dist/esm/icons/loader-2';
import { Button } from '@/shared/ui/button';
import { Card } from '@/shared/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/shared/ui/table';
import { Alert, AlertDescription, AlertTitle } from '@/shared/ui/alert';
import { ScrollArea } from '@/shared/ui/scroll-area';
import { ExcelImporterProps, ImporterState, ValidationResult } from './types';
import { parseExcelFile, validateExcelData } from './utils';
import { toast } from 'sonner';

/**
 * 閫氱敤 Excel 瀵煎叆缁勪欢
 */
export function ExcelImporter<T extends Record<string, any>>({
    schema,
    onImport,
    templateUrl,
    columnMapping,
    title = '瀵煎叆鏁版嵁',
    description = '鏀寔 .xlsx, .xls, .csv 鏍煎紡鏂囦欢',
}: ExcelImporterProps<T>) {
    const [state, setState] = useState<ImporterState>('idle');
    const [results, setResults] = useState<ValidationResult<T>[]>([]);
    const [fileName, setFileName] = useState<string>('');

    const onDrop = useCallback(async (acceptedFiles: File[]) => {
        const file = acceptedFiles[0];
        if (!file) return;

        setFileName(file.name);
        setState('parsing');

        try {
            const rawData = await parseExcelFile(file);
            const validationResults = validateExcelData(rawData, columnMapping, schema);
            setResults(validationResults);
            setState('previewing');
        } catch (error) {
            console.error('Excel parse error:', error);
            toast.error('鏂囦欢瑙ｆ瀽澶辫触锛岃妫€鏌ユ枃浠舵牸寮?);
            setState('idle');
        }
    }, [columnMapping, schema]);

    // 杩欓噷鎴戞殏鏃跺厛鐢ㄥ師鐢熺殑 Drag Event锛屽洜涓?package.json 娌＄湅鍒?react-dropzone
    // 浣嗕负浜嗘洿濂界殑浣撻獙锛屾垜浼氱◢鍚庡喅瀹氭槸鍚﹀畨瑁呫€傜洰鍓嶅厛瀹炵幇鍩虹閫昏緫銆俓r

    const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            await onDrop([file]);
        }
    };

    const handleImport = async () => {
        const validData = results
            .filter((r) => r.data !== null)
            .map((r) => r.data as T);

        if (validData.length === 0) {
            toast.error('娌℃湁鍙鍏ョ殑鏈夋晥鏁版嵁');
            return;
        }

        setState('importing');
        try {
            await onImport(validData);
            toast.success(`鎴愬姛瀵煎叆 ${validData.length} 鏉℃暟鎹甡);
            setState('success');
        } catch (error) {
            toast.error('瀵煎叆澶辫触锛岃绋嶅悗閲嶈瘯');
            setState('previewing');
        }
    };

    const reset = () => {
        setState('idle');
        setResults([]);
        setFileName('');
    };

    const errorCount = results.filter(r => r.errors !== null).length;
    const validCount = results.length - errorCount;

    return (
        <Card className="p-6 w-full max-w-4xl mx-auto border-dashed border-2">
            <div className="space-y-4">
                <div className="flex justify-between items-center">
                    <div>
                        <h3 className="text-lg font-semibold">{title}</h3>
                        <p className="text-sm text-muted-foreground">{description}</p>
                    </div>
                    {templateUrl && (
                        <Button variant="outline" size="sm" asChild>
                            <a href={templateUrl} download>涓嬭浇妯℃澘</a>
                        </Button>
                    )}
                </div>

                {state === 'idle' && (
                    <div
                        className="border-2 border-dashed rounded-lg p-12 text-center hover:bg-muted/50 transition-colors cursor-pointer relative"
                        onClick={() => document.getElementById('excel-upload')?.click()}
                    >
                        <input
                            id="excel-upload"
                            type="file"
                            className="hidden"
                            accept=".xlsx,.xls,.csv"
                            onChange={handleFileChange}
                        />
                        <Upload className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                        <p className="text-sm font-medium">鐐瑰嚮鎴栨嫋鎷芥枃浠惰嚦姝ゅ涓婁紶</p>
                        <p className="text-xs text-muted-foreground mt-1">鏈€澶ф敮鎸?10MB</p>
                    </div>
                )}

                {state === 'parsing' && (
                    <div className="py-12 text-center space-y-4">
                        <Loader2 className="h-8 w-8 animate-spin mx-auto text-primary" />
                        <p className="text-sm text-muted-foreground">姝ｅ湪瑙ｆ瀽鏂囦欢骞舵牎楠屾暟鎹?..</p>
                    </div>
                )}

                {(state === 'previewing' || state === 'importing') && (
                    <div className="space-y-4">
                        <div className="flex items-center justify-between bg-muted/50 p-3 rounded-md">
                            <div className="flex items-center gap-4 text-sm">
                                <span className="flex items-center gap-1 text-green-600 font-medium">
                                    <CheckCircle2 className="h-4 w-4" /> {validCount} 鏉℃湁鏁圽r
                                </span>
                                {errorCount > 0 && (
                                    <span className="flex items-center gap-1 text-destructive font-medium">
                                        <AlertCircle className="h-4 w-4" /> {errorCount} 鏉￠敊璇痋r
                                    </span>
                                )}
                                <span className="text-muted-foreground">鏂囦欢鍚? {fileName}</span>
                            </div>
                            <Button variant="ghost" size="sm" onClick={reset}>閲嶆柊涓婁紶</Button>
                        </div>

                        {errorCount > 0 && (
                            <Alert variant="destructive">
                                <AlertCircle className="h-4 w-4" />
                                <AlertTitle>鏁版嵁鏍￠獙鏈€氳繃</AlertTitle>
                                <AlertDescription>
                                    璇蜂慨姝ｄ互涓嬮敊璇悗閲嶆柊涓婁紶锛屾垨鑰呮偍鍙互浠呭鍏ユ湁鏁堟暟鎹€俓r
                                </AlertDescription>
                            </Alert>
                        )}

                        <ScrollArea className="h-[300px] border rounded-md">
                            <Table>
                                <TableHeader>
                                    <TableRow>
                                        <TableHead className="w-16">琛屽彿</TableHead>
                                        {Object.keys(columnMapping).map(header => (
                                            <TableHead key={header}>{header}</TableHead>
                                        ))}
                                        <TableHead>鏍￠獙缁撴灉</TableHead>
                                    </TableRow>
                                </TableHeader>
                                <TableBody>
                                    {results.map((res, i) => (
                                        <TableRow key={i} className={res.errors ? 'bg-destructive/5' : ''}>
                                            <TableCell className="font-mono text-xs">{res.rowNumber}</TableCell>
                                            {Object.keys(columnMapping).map(header => (
                                                <TableCell key={header}>
                                                    {String(res.raw[header] ?? '-')}
                                                </TableCell>
                                            ))}
                                            <TableCell>
                                                {res.errors ? (
                                                    <div className="text-destructive text-xs space-y-1">
                                                        {res.errors.map((err, ei) => (
                                                            <div key={ei} className="flex items-start gap-1">
                                                                <AlertCircle className="h-3 w-3 mt-0.5 shrink-0" />
                                                                {err}
                                                            </div>
                                                        ))}
                                                    </div>
                                                ) : (
                                                    <CheckCircle2 className="h-4 w-4 text-green-600" />
                                                )}
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </ScrollArea>

                        <div className="flex justify-end gap-2">
                            <Button variant="outline" onClick={reset} disabled={state === 'importing'}>
                                鍙栨秷
                            </Button>
                            <Button
                                onClick={handleImport}
                                disabled={state === 'importing' || results.length === 0}
                            >
                                {state === 'importing' && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                                纭瀵煎叆 {validCount} 鏉℃暟鎹甛r
                            </Button>
                        </div>
                    </div>
                )}

                {state === 'success' && (
                    <div className="py-12 text-center space-y-4">
                        <CheckCircle2 className="h-12 w-12 mx-auto text-green-600" />
                        <h4 className="text-xl font-semibold">瀵煎叆鎴愬姛</h4>
                        <p className="text-sm text-muted-foreground">鎮ㄧ殑鏁版嵁宸叉垚鍔熷鍏ョ郴缁熴€?/p>
                        <Button onClick={reset}>缁х画瀵煎叆鍏朵粬鏂囦欢</Button>
                    </div>
                )}
            </div>
        </Card>
    );
}

'use client';

import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/shared/ui/dialog";
import { Button } from "@/shared/ui/button";
import { X, ZoomIn, ZoomOut, RotateCw, Download } from "lucide-react";
